function switchTab(e,a){for(var t,n=document.getElementsByClassName("kanboardtabcontent"),s=0;s<n.length;s++)n[s].style.display="none";for(t=document.getElementsByClassName("kanboardtablinks"),s=0;s<t.length;s++)t[s].className=t[s].className.replace(" active","");document.getElementById(a).style.display="block",e.currentTarget.className+=" active"}